<template>
  <div class="event-content" :class="{ passed: isActual === false }">
    <div>
      <img
        :src="
          event.cover
            ? event.cover
            : 'data:image/png;base64,'
        "
      />

      <h4>
        {{ (event.icon || "") + " " + event.title }}
      </h4>

      <h5>
        {{ event.subtitle }}
      </h5>

      <div class="dates">
        <a
          v-if="event.link"
          key="link"
          :href="
            event.link.includes('http') ? event.link : `https://${event.link}`
          "
          target="_blank"
          rel="noreferrer"
        >
          {{
            event.link
              .replace(`https://`, "")
              .replace(`http://`, "")
              .split("/")[0]
          }}
        </a>
      </div>

      <div class="dates">
        <span>
          {{
            new Date(event.date.start)
              .toLocaleDateString("de-DE", {
                year: "numeric",
                month: "short",
                day: "numeric",
                hour: "2-digit",
                minute: "2-digit",
              })
              .replace(", 00:00", "")
          }}
        </span>
        <span>
          {{
            event.date.end &&
            " -  " +
              new Date(event.date.end)
                .toLocaleDateString("de-DE", {
                  year: "numeric",
                  month: "short",
                  day: "numeric",
                  hour: "2-digit",
                  minute: "2-digit",
                })
                .replace(", 00:00", "")
          }}
        </span>
      </div>

      <div class="content">
        <p>
          {{ event.description.join("") }}
        </p>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "component-event",
  props: ["event", "isActual"],
};
</script>

